import React from 'react';
import SearchView from './components/SearchView';
import data from './data.json'
import styles from './App.module.css';
import Header from './components/Header';

class App extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      items: data.items,
      productSearchString: "",
    }
  }


  onSearchFieldChange = (event) => {

    console.log('Keyboard event');
    console.log(event.target.value);
    this.setState({ productSearchString: event.target.value });
  }

  

  render()
  {
    let output =
      <div>
      <Header />
      <div className={styles.paasivubar}>
      <div ClassName={styles.hakukone} > <input type="text"  onChange={ this.onSearchFieldChange } value={ this.state.productSearchString }/>Ravintolahaku</div>       
      <SearchView
          items={ this.state.items.filter((item) => item.name.toLowerCase().includes(this.state.productSearchString.toLowerCase())) }
          />
          
        </div>
        </div>
        


    

    return (
      <>
        { output }
      </>
    )
  }
}

export default App;
