import React from 'react';
import SearchView from './components/SearchView';
import data from './data.json'
import styles from './header.module.css';


class App extends React.Component {
  constructor(props)
  {
    super(props);
    this.state = {
      items: data.items,
      productSearchString: "",
    }
  }


  onSearchFieldChange = (event) => {

    console.log('Keyboard event');
    console.log(event.target.value);
    this.setState({ productSearchString: event.target.value });
  }

  

  render()
  {
    let output =
      <>
      <div className={styles.header}>
      <div className={styles.logo}>Woltti</div>
        <div className={styles.search}>
           Ravintolahaku <input type="text" onChange={ this.onSearchFieldChange } value={ this.state.productSearchString }/></div>
           <div className={styles.uloskirjaus}>Kirjaudu ulos</div>
           <div className={styles.ostoskori}>Ostoskori</div>
           
        </div>
        <SearchView
          items={ this.state.items.filter((item) => item.name.toLowerCase().includes(this.state.productSearchString.toLowerCase())) }
          />
      </>


    

    return (
      <>
        { output }
      </>
    )
  }
}

export default App;
